SELECT ticker, (enterpriseValue/1000000), magicFormulaRatio, EVtoEBITDA, returnOnIC, returnOnEquity, totalDebtToEquity, (totalCash/totalDebt), (revenue / 1000000), (ebitda / 1000000), fiftyTwoWeekChange, fiftyDayMovingAvg FROM _security WHERE evtoebitda < 10 AND evtoebitda > 0  AND ebitda > 0  AND priceBook < 6 AND operatingCashFlow > 0 AND returnOnEquity > .05 AND percentHeldByInstitutions > .1 AND profitMargin > .05 AND returnOnAssets > .05 AND totalDebtToEquity < 50 AND fiftyTwoWeekChange > 0 AND ((marketCap / sharesOutstanding) - fiftyDayMovingAvg) > 0 ORDER BY magicFormulaRatio DESC;