SELECT ticker, (enterpriseValue/1000000), magicFormulaRatio, EVtoEBITDA, returnOnIC, returnOnEquity, totalDebtToEquity, (totalCash/totalDebt), (revenue / 1000000), (ebitda / 1000000), fiftyTwoWeekChange, fiftyDayMovingAvg FROM _security WHERE qtrRevenueGrowth > 0 AND qtrEarningsGrowth > 0 AND magicFormulaRatio > .01 AND fiftyTwoWeekChange > 0 AND ((marketCap / sharesOutstanding) - fiftyDayMovingAvg) > 0 AND totalDebtToEquity < 30 AND returnOnEquity > .02;